import React, { Fragment, useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useShowNavigationItems } from "../../../store/actions/actionsIndex";
import {
  NavigationPanel,
  HBWr,
  HBInnerWr,
  HBSpan,
  Logo,
  LogoWr,
  Cart,
  CartCounter,
} from "../../../StyledComponents/toolbarItems";
import { Bee } from "../../../public/Icon/Icon";
import Link from "next/link";
import { faShoppingBag } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Dropdown from "../../../UI/Dropdown/Dropdown";

const Toolbar = ({ visible }) => {
  const dispatch = useDispatch();
  const [globalWindow, setGlobalWindow] = useState(null);
  const navItemsActive = useSelector((state) => state.navigation.showNavItems);
  const navItems = useSelector((state) => state.navigation.navItems);

  useEffect(() => {
    setGlobalWindow(window);
  }, []);

  let topIconStyle = !visible ? { top: "-10vh" } : { top: "1rem" };
  let bottomIconStyle = !visible ? { bottom: "-10vh" } : { bottom: "1rem" };

  const iconVisibilityMixin = (visible, icon) => {
    const topIcon = !visible && icon === "top" ? { top: "-10vh" } : { top: "1rem" };
    const bottomIcon = !visible && icon === "bottom" ? { bottom: "-10vh" } : { bottom: "1rem" };
    return icon === 'top' ? topIcon : icon === "bottom" ? bottomIcon : alert('icon function is wrong');
  }


  return (
    <Fragment>
      {/* <NavigationPanel> for desktop only */}
      <NavigationPanel
        tabIndex="0"
        active={navItemsActive}
        style={topIconStyle}
        visible={visible}
      >
        <ProductLogo />
        <HamburgerButton
          active={navItemsActive}
          toggleNavItems={() => dispatch(useShowNavigationItems())}
        />
      </NavigationPanel>
      {/* <HBWr> & <LogoWr> for mobile only */}
      <HBWr
        active={navItemsActive}
        style={bottomIconStyle}
        visible={visible}
        onClick={() => dispatch(useShowNavigationItems())}
      >
        <HamburgerButton />
      </HBWr>
      <LogoWr active={navItemsActive} visible={visible} style={topIconStyle}>
        <ProductLogo />
      </LogoWr>
      {/* <ShoppingCart> & <Dropdown> for any size device */}
      <ShoppingCart
        visible={visible}
        windowWidth={globalWindow?.innerWidth}
        style={globalWindow?.innerWidth < 640 ? bottomIconStyle : topIconStyle}
      />
      <Dropdown
        dropDownTogglerHandler={() => dispatch(useShowNavigationItems())}
        subItems={navItems}
        showDropDown={navItemsActive}
        useOnToolbar
      />
    </Fragment>
  );
};

export default Toolbar;

const HamburgerButton = ({ active, toggleNavItems }) => {
  return (
    <HBInnerWr active={active} onClick={toggleNavItems}>
      <HBSpan />
      <HBSpan />
      <HBSpan />
    </HBInnerWr>
  );
};

const ProductLogo = () => (
  <Logo>
    <Link href="/">
      <a>
        <Bee />
      </a>
    </Link>
  </Logo>
);

const ShoppingCart = ({ style }) => (
  <Cart style={style}>
    <FontAwesomeIcon icon={faShoppingBag} size="2x" />
    <CartCounter>
      <span>3</span>
    </CartCounter>
  </Cart>
);
