import styled, { css } from "styled-components";

const navItemsFlexStyles = css`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const wrapperDims = css`
  width: 100vw;
  height: 100vh;
`;

export const Wrapper = styled.div`
  position: fixed;
  ${wrapperDims};
  background: linear-gradient(to top, rgba(86, 20, 238, 0.5) 100%, transparent), ${({ withBackground }) =>
    withBackground ? `url(${withBackground})` : "rgb(115, 73, 247)"} center center/cover no-repeat fixed;


  ${navItemsFlexStyles};
`;

export const List = styled.ul`
  ${wrapperDims};
  margin: 0;
  padding: 0;
  display: flex;
  ${navItemsFlexStyles};
  background: ${({withBackground}) => withBackground ? "transparent" : "rgba(86, 20, 238, 1)"};
  transition: 500ms all ease-out;
`;

export const MenuItem = styled.li`
  ${navItemsFlexStyles};
  margin: 1rem;
  font-weight: bold;
  font-size: 2.2rem;
  a {
    text-align: center;
    color: white;
    padding: 2rem;
  }
  &,
  a {
    width: 100vw;
  }
`;
