import React, { Fragment } from "react";
import "./Toolbar.scss";
// import Logo from "../../../UI/Logo/Logo";
// import Cart from "../../../UI/Cart/Cart";
// import Dropdown from "../Dropdown/Dropdown";
// import HamburgerButton from "../HamburgerButton/HamburgerButton";
import { connect } from "react-redux";
import * as actionCreators from '../../../Store/Actions/index'

const Toolbar = (props) => {
 const {navItemsActive, navItems, visible, onShowNavigationItems} = props;

  let toolbarIconHidden = {
    top: "-10vh",
  };

  let toolbarIconShow = {
    bottom: "0",
  };

  let topIconStyle = !visible ? { top: "-10vh"} : {bottom: 0};
  let bottomIconStyle = !visible ? {bottom: "-10vh"} : {bottom: "2rem"};

  const iconStyles = !visible ? toolbarIconHidden : toolbarIconShow;
  const navigationPanelClasses = ["NavigationPanel", navItemsActive ? "NavigationPanel__active" : null]
  const hamburgerButtonClasses= ["Hamburger__button__wrapper", navItemsActive ? "Hamburger__button__wrapper__active" : null];
  const logoClasses = ["Logo__wrapper", navItemsActive ? "Logo__wrapper__active" : null];

  return (
    <Fragment>
      <div className={navigationPanelClasses.join(' ')} style={iconStyles}>
        {/* <Logo />
        <HamburgerButton /> */}
      </div>
      <div style={bottomIconStyle} className={hamburgerButtonClasses.join(' ')}>
        <HamburgerButton />
      </div>
      <div style={iconStyles} className={logoClasses.join(' ')}>
      <Logo />
      </div>
      <Dropdown
          dropDownTogglerHandler={() => onShowNavigationItems()}
          subItems={navItems}
          withHamburger
          showDropDown={navItemsActive}
        />
      <Cart style={window.innerWidth < 640 ? bottomIconStyle : topIconStyle} />
    </Fragment>
  );
};

const mapStateToProps = (state) => {
  return {
    navItemsActive: state.navigationReducer.showNavItems,
    navItems: state.navigationReducer.navItems,
  };
};

const mapDispatchToProps = dispatch => {
  return {
      onShowNavigationItems: () => dispatch(actionCreators.showNavigationItems())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Toolbar);
