import styled, { css, keyframes } from "styled-components";

export const MyLink = styled.a`
  color: red;

  ${(props) =>
    props.primary &&
    css`
      color: green;
    `}
`;

const buttonTypeHandler = type => {
  switch(type) {
    case "success": return `
    color: green; 
    background-color: yellow;
    `
    case "danger": return `
    color: yellow;
    background-color: salmon;
    `
    default: return null;
  }
}

export const MyButton = styled.button`
  background-color: blue;
  color: white;
  font-weight: bold;
  padding: 1.2rem;
  border-radius: 5px;
  margin: .5rem 0;
  pointer
  ${({type}) => buttonTypeHandler(type)};
`;

export const DangerButton = styled(MyButton)`
 color: yellow;
 background-color: red;
`;

export const CoolTitle = styled.h1`
  border-bottom: 2px solid green;
  transition: 600ms all ease;
  cursor: pointer;
  &:hover {
    color: red;
  }
`;

const slide = keyframes`
0% {
  transform: translateX(-100vw);
  opacity: 0;
}

100% {
  transform: translateX(0);
  opacity: 1;
}
`;

export const Section = styled.section`
  background-color: yellow;
  margin: 1rem 0;
  padding: 1rem;
  width: 80vw;
  border: 1px solid black;
  opacity: 0;
  animation: ${slide} 1s ease-in forwards;
`;
