import styled, {keyframes, css} from 'styled-components';
import {zIndex} from './Library/variables'

const backdropEntering = keyframes`
to {opacity: 1}
`
const backdropLeaving = keyframes`
to {opacity: 0}
`
const backdropEnterMixin = () => css`
animation: ${backdropEntering} 400ms ease forwards;
`

const backdropLeaveMixin = () => css`
animation: ${backdropLeaving} 400ms ease forwards;
`

export const Backdrop = styled.div`
z-index: ${zIndex.backdrop};
position: fixed;
left: 0;
top:0;
bottom: 0;
right: 0;
width: 100vw;
height: 100vh;
opacity: 0;
background: rgba(0,0,0,0.5);
${({ state }) =>
    state === "entering" || state === "entered"
      ? backdropEnterMixin
      : state === "exiting" || state == "exited"
      ? backdropLeaveMixin
      : null};`
