import styled, {keyframes, css} from 'styled-components';
import {zIndex} from './Library/variables'

export const modalEntering = keyframes`
to {opacity: 1}
`
const modalLeaving = keyframes`
to {opacity: 0}
`
export const modalEnter = () => css`
animation: ${modalEntering} 400ms ease forwards;
`

export const modalLeave = () => css`
animation: ${modalLeaving} 400ms ease forwards;
`

export const Backdrop = styled.div`
cursor: pointer;
z-index: ${zIndex.backdrop};
position: fixed;
left: 0;
top:0;
bottom: 0;
right: 0;
width: 100vw;
height: 100vh;
opacity: 0;
background: rgba(0,0,0,0.5);
${({ state }) =>
    state === "entering" || state === "entered"
      ? modalEnter
      : state === "exiting" || state == "exited"
      ? modalLeave
      : null};`
