import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTwitter,
  faFacebook,
  faInstagram,
} from "@fortawesome/free-brands-svg-icons";
import {
  LinkWrapper,
  SocialMediaWrapper,
  Link,
} from "../../StyledComponents/socialMediaItems";

const SocialMedia = ({ iconSize, pattern }) => {
  let menuIconSize = pattern === 'menu' && window.innerWidth < 640 ? '3x' : '4x';
  let footerIconSize = pattern === 'footer' 
  return (
    <SocialMediaWrapper pattern={pattern}>
      <LinkWrapper>
        <Link pattern={pattern}>
          <FontAwesomeIcon icon={faTwitter} size={menuIconSize} />
        </Link>
      </LinkWrapper>
      <LinkWrapper>
        <Link pattern={pattern}>
          <FontAwesomeIcon icon={faFacebook} size={menuIconSize} />
        </Link>
      </LinkWrapper>
      <LinkWrapper>
        <Link pattern={pattern}>
          <FontAwesomeIcon icon={faInstagram} size={menuIconSize} />
        </Link>
      </LinkWrapper>
    </SocialMediaWrapper>
  );
}

export default SocialMedia;
