import React, { useState, useEffect } from 'react';

export function useWindowDimension() {
  const [clientWindow, setClientWindow] = useState(undefined);

  const [dimension, setDimension] = useState([]);

  useEffect(() => {
    setClientWindow(window)
  },[])

  useEffect(() => {
   if(clientWindow !== undefined) {
    const debouncedResizeHandler = debounce(() => {
      setDimension([clientWindow.innerWidth, clientWindow.innerHeight]);
    }, 100); // 100ms
    window.addEventListener('resize', debouncedResizeHandler);
   }
    return () => window.removeEventListener('resize', debouncedResizeHandler);
  }, [clientWindow]); // Note this empty array. this effect should run only on mount and unmount

  return dimension;
}

function debounce(fn, ms) {
  let timer;
  return _ => {
    clearTimeout(timer);
    timer = setTimeout(_ => {
      timer = null;
      fn.apply(this, arguments);
    }, ms);
  };
}
