  console.log("initial props", props);

  return <div>product details...</div>
};

export default ProductDetails;

ProductDetails.getInitialProps =  async (context) => {
  const category = context.query.activeCategory;
  const id = context.query.id;
  const product = context.query.product;
  const res = await fetch(`https://meli-99509.firebaseio.com/catalog/${product}/${category}/${id}.json`)
  const json = await res.json()

  return {
    product: json
