import {createRef, useState} from 'react'

export const useElementCurrent = (ref, index) => {
    const reff = ref;
    console.log("reff",ref)
    const catLength = categories.length;
    const [elRefs, setElRefs] = useState([]);

    useEffect(() => {
      setElRefs((elRefs) =>
        Array(catLength)
          .fill()
          .map((_, i) => elRefs[i] || createRef())
      );
    }, []);
}
