import styled, { css } from "styled-components";
import { toolBarMixin, hamburgerSpanActive } from "./Library/mixins";
import { mainColors } from "./Library/variables";

export const NavigationPanel = styled.div`
  display: none;

  @media (min-width: 40rem) {
    ${toolBarMixin(1, 1)};
    width: 9rem;
    height: 4rem;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #fdf3e6;
    border-radius: 35px;
    z-index: 35;
    transition: all 500ms ease-in;

    &:hover {
      background-color: rgb(221, 193, 221);
    }

    ${({ active }) =>
      active === true &&
      css`
        background-color: #f29b38;
        & path {
          fill: white;
        }

        &:hover {
          background-color: #f29b38;

          & path {
            fill: white;
          }
        }
      `}
  }
`;

export const HBWr = styled.div`
  ${toolBarMixin(null, 1, null, 1)};
  width: 3.2rem;
  height: 3.2rem;
  border-radius: 50%;
  background-color: #fdf3e6;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 35;
  @media (min-width: 40rem) {
    display: none;
  }
  ${({ active }) =>
    active === true &&
    css`
      background-color: ${mainColors.accentOrange};
      ${hamburgerSpanActive}
    `}
`;

export const HBInnerWr = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  width: 2rem;
  height: 2rem;
  z-index: 30;
  cursor: pointer;
`;

export const HBSpan = styled.span`
  width: 80%;
  height: 4px;
  background-color: #f29b38;
  border-radius: 10px;
  transition: all 0.5s ease-in-out;
`;

const LogoWr = styled.li`
 ${tool(1, 1, null, null)};
  width: 3.2rem;
  height: 3.2rem;
  border-radius: 50%;
  background-color: #fdf3e6;
  z-index: 35;
  display: flex;
  justify-content: center;
  align-items: center;

  & li, 
  & a, 
  & svg {
    margin-right: 0;
    width: 44px;
    height: 42px;
  }

  &__active {

    background-color: #f29b38;
    & path {
      fill: white;
    }
  }
`
