import styled, { css } from "styled-components";

const navItemsFlexStyles = css`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const wrapperDims = css`
  width: 100vw;
  height: 100vh;
`;

export const Wrapper = styled.div`
  position: fixed;
  transition: all 400ms ease-in;
  ${wrapperDims};
  background: linear-gradient(to top, rgba(86, 20, 238, 0.5) 100%, transparent), ${({ withBackground }) =>
    withBackground ? `url(${withBackground})` : "transparent"} center center/cover no-repeat fixed;


  ${navItemsFlexStyles};
`;

export const List = styled.ul`
  ${wrapperDims};
