import React from "react";
import cssClasses from "./home.module.scss";
import Link from "next/link";
import styled, { css, ThemeProvider } from "styled-components";
import NavLink from "../Components/NavLink/NavLink";
import {
  MyLink,
  MyButton,
  CoolTitle,
  Section,
  DangerButton,
} from "../Components/StyledComponents/styledComponents";

const Input = styled.input.attrs({
  type: "number",
  size: 2,
  placeholder: "Your number",
})`
  color: red;
  text-decoration: none;
  :disabled {
    border: red 20px solid;
  }
  ${({ foo, borderWidth }) =>
    foo &&
    css`
      color: purple;
      padding: 0.3rem;
      outline: none;
      border-width: ${borderWidth};
      ::-webkit-inner-spin-button,
      ::-webkit-outer-spin-button {
        -webkit-appearance: none;
      }
      ::placeholder {
        color: purple;
      }
      &:focus {
        border-color: red;
        padding: 0.3rem;
      }
    `}
`;

const Div = styled.div.withConfig({
  shouldForwardProp: (prop, defaultValidatorFn) =>
    !["foo"].includes(prop) && defaultValidatorFn(prop),
})`
  :disabled {
    border: gray 200px solid;
  }
  ${({ foo }) => `
 background-color: ${foo}
 `}
`;

const FormInput = styled.input.attrs({
  type: "text",
})`
  border-color: blue;
  color: red;
  background-color: ${(props) => props.theme.background}
    ${({ $size }) => `
  height: ${$size}rem;
  width: ${$size}rem;;
  `};
`;

const Label = styled(FormInput)``;

const App = () => {
  return (
    <div className={cssClasses.Joblers}>
      <ThemeProvider theme={{ backgroundColor: "red" }}>
        <h1 className={cssClasses.Joblers__title}>Hello NextJs</h1>
        <Input foo borderWidth="3px" />
        <Div disabled foo="yellow">
          Styled Div
        </Div>
        <Link href={{ pathname: "/about", query: { name: "test" } }}>
          <a>About us</a>
        </Link>
        <FormInput as="div" $size="4">
          aaaa
        </FormInput>
        <Label as="label">Label</Label>
      </ThemeProvider>
    </div>
  );
};

export default App;
