import React, { useEffect } from "react";
import { useStore } from "react-redux";
import {
  Wrapper,
  Banner,
  BannerImage,
  Details,
  Title,
  Features,
  Feature,
  Panel,
  Add,
  Remove,
  Qty
} from "../../StyledComponents/productDetails";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlusCircle, Circle} from '@fortawesome/free-solid-svg-icons'

const ProductDetails = ({ product }) => {
  console.log("initial props", product);

  return (
    <Wrapper>
      <Banner>
        <BannerImage src="/DetailsTest/test.jpg" alt="banner logo" />
      </Banner>
      <Details>
      <Title>Caf√© Black</Title>
      <Features>
      <Feature>Sobre</Feature>
      <Feature>Como usar</Feature>
      <Feature>Ingredientes</Feature>
      <Feature>Organic</Feature>
      </Features>
      <Panel>
        <Cta>Compre Agora</Cta>
        <Panel>
          <Remove></Remove>
          <Qty></Qty>
          <Add></Add>
        </Panel>
      </Panel>
      </Details>
    </Wrapper>
  );
};

export default ProductDetails;

export async function getServerSideProps(context) {
  const product = context.query.product;
  const category = context.query.activeCategory;
  const id = context.query.id;
  const res = await fetch(
    `https://meli-99509.firebaseio.com/catalog/${product}/${category}/${id}.json`
  );
  const json = await res.json();

  return {
    props: {
      product: json,
    },
  };
}
