import * as actions from "../actions/productsActions";
import { setState } from "../../Utilities/utilities";

const initialState = {
  activeCatalog: "bioCloths",
  activeCategory: "allSizes",
  overview: {
    bioCloths: {
      label: "BIO EMBALAGENS",
      background: "/Products/Cloth3.jpg",
      path: "bio-embalagens",
      products: [
        { id: "allSizes", label: "MIX" },
        { id: "medium", label: "MEDIO" },
        { id: "big", label: "GRANDE" },
      ],
    },
    soapWorks: {
      label: "SABOARIA",
      background: "/Products/saboaria1.jpg",
      path: "saboaria",
      products: [
        { id: "ofuro", label: "SAIS" },
        { id: "greenClay", label: "SKINCARE" },
        { id: "shampooConditioner", label: "CABELOS" },
        { id: "soap", label: "SABONETE" },
      ],
    },
  },
  catalog: {
    bioCloths: {
      allSizes: {
        1: {
          images: {
            paths: {}
          },
          name: "Amarelinho",
          stock: 20,
          price: 10,
        },
      },
      medium: {
        1: {
          images: ["Cloth2"],
          name: "Florestal",
          stock: 10,
          price: 50,
        },
      },
      big: {
        1: {
          images: ["Cloth3"],
          name: "Joblinho",
          stock: 40,
          price: 70,
        },
      },
    },
    soapWorks: {
      ofuro: {
        1: {
          images: ["Soap1"],
          name: "Bebê Soninho",
          stock: 20,
          price: 10,
        },
      },
      greenClay: {
        1: {
          images: ["Soap1"],
          name: "Beleza Total",
          stock: 20,
          price: 10,
        },
      },
      shampooConditioner: {
        1: {
          images: ["Soap1"],
          name: "Restauração Capilar",
          stock: 20,
          price: 10,
        },
      },
      soap: {
        1: {
          images: ["Soap1"],
          name: "Hidrata Tudo",
          stock: 20,
          price: 10,
        },
        2: {
          images: ["Soap2"],
          name: "Esfolia Sua Alma",
          stock: 20,
          price: 10,
        },
        3: {
          images: ["Soap3"],
          name: "Hortelã Mentolado",
          stock: 20,
          price: 10,
        },
        4: {
          images: ["Soap3"],
          name: "Cacau com Chocolate",
          stock: 20,
          price: 10,
        },
      },
    },
  },
  details: {
    bioCloths: {
      allSizes: {},
      medium: {},
      big: {}
    },
    soapWorks: {
      ofuro: {},
      greenClay: {},
      shampooConditioner: {},
      soap: {}
    }
  }
};

const toggleCatalog = (state, payload) => {
  return setState({
    ...state,
    activeCatalog: payload.catalog,
    activeCategory: payload.category,
  });
};

const toggleCategory = (state, payload) => {
  return setState({ ...state, activeCategory: payload.category });
};

export const productsReducer = (state = initialState, payload) => {
  switch (payload.type) {
    case actions.TOGGLE_CATALOG:
      return toggleCatalog(state, payload);
    case actions.TOGGLE_CATEGORY:
      return toggleCategory(state, payload);
    default:
      return state;
  }
};
