import { css } from "styled-components";
import { zIndex, mainColors } from "./variables";

export const toolBarMixin = (
  top = null,
  left = null,
  right = null,
  bottom = null
) => {
  return css`
    position: fixed;
    top: ${top}rem;
    left: ${left}rem;
    right: ${right}rem;
    bottom: ${bottom}rem;
    z-index: ${zIndex.toolbar};
    transition: all 400ms ease-in;
  `;
};

export const hamburgerSpanActive = css`
   span {
    background-color: ${mainColors.orange};
  }

  span:nth-child(1) {
    width: 100%;
    transform-origin: left;
    transform: rotate(45deg) translate(5%, -240%);
  }

  span:nth-child(2) {
    transform: translateX(-100%);
    opacity: 0;
  }

  span:nth-child(3) {
    width: 100%;
    transform-origin: left;
    transform: rotate(-45deg) translate(5%, 240%);
  }
`;
