import { useRef, useEffect } from "react";

const isBrowser = typeof window !== `undefined`;

const getScrolledPosition = ({ element, useWindow }) => {
  if (!isBrowser) return { x: 0, y: 0 };

  const target = element ? element.current : document.body;
  const position = target.getBoundingClientRect();
  return useWindow
    ? { x: window.scrollX, y: window.scrollY }
    : {
        x: position.left,
        y: position.top,
        height: position.height,
        bottom: position.bottom,
      };
};

export const useScrollPosition = (effect, deps, element, useWindow, wait) => {
  const position = useRef(getScrolledPosition({ useWindow }));
  let throttleTimeout = null;
  const callBack = () => {
    const currPos = getScrolledPosition({ element, useWindow });
    effect({ prevPos: position.current, currPos });
    position.current = currPos;
    throttleTimeout = null;
  };
  useEffect(() => {
    const handleScroll = () => {
      if (wait) {
        if (throttleTimeout === null) {
          throttleTimeout = setTimeout(callBack, wait);
        }
      } else {
        callBack();
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, deps);
};
