import styled, { css, keyframes } from "styled-components";

const navItemsFlexStyles = css`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const wrapperDims = css`
  width: 100vw;
  height: 100vh;
`;

export const Wrapper = styled.div`
  position: fixed;
  ${wrapperDims};
  ${navItemsFlexStyles};
  background: rgba(86, 20, 238, 1);
`;

const enterBackground = keyframes`
to {opacity: 1}
`;
const leaveBackground = keyframes`
to {opacity:0}
`;

export const List = styled.ul`
  ${wrapperDims};
  margin: 0;
  padding: 0;
  display: flex;
  ${navItemsFlexStyles};
`;

export const Background = styled.div`
 position: fixed;
  margin: 0;
  padding: 0;
  display: flex;
  opacity: 0;
  ${navItemsFlexStyles};
  ${wrapperDims};
  ${({withoutBackground}) => withoutBackground && css`animation: ${leaveBackground} 5000ms ease forwards;`};
  ${({withBackground}) => withBackground && css` animation: ${enterBackground} 2000ms ease forwards;`}
  background: linear-gradient(to top, rgba(86, 20, 238, 0.5) 100%, transparent),
    ${({ withBackground }) =>
        withBackground ? `url(${withBackground})` : "rgb(135, 73, 250)"}
      center center/cover no-repeat fixed;
`

export const MenuItem = styled.li`
z-index: 1;
  ${navItemsFlexStyles};
  margin: 1rem;
  font-weight: bold;
  font-size: 2.2rem;
  a {
    text-align: center;
    color: white;
    padding: 2rem;
  }
  &,
  a {
    width: 100vw;
  }
`;
