height: 100%;
width: 100%;
`

export const Banner = styled.div`
width: 100%;
height: 50vh;
`

export const BannerImage = styled.img`
object-fit: cover;
width: 100%;
height: 100%;
`

export const Details = styled.section`
padding-top: 2rem;
display: flex;
flex-direction: column;
align-content: center;
text-align: center;
min-height: 100vh;
`

export const Title = styled.h1`
font-size: 3rem;
margin: 1rem auto;
width: 80%;
z-index: 1;
text-align: center;

