import React, { Fragment, useState, useEffect } from "react";
import { Transition } from "react-transition-group";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";
import { useScrollPosition } from "../../Hooks/useScrollPosition";
import Link from "next/link";
import { Wrapper, List, MenuItem } from "../../StyledComponents/dropdownItems";

const NavItem = ({ path, label, mouseEntered, mouseLeft }) => {
  return (
    <MenuItem onMouseLeave={mouseLeft} onMouseEnter={mouseEntered}>
      <Link href={path}>
        <a>{label}</a>
      </Link>
    </MenuItem>
  );
};

const Dropdown = ({
  dropDownTitle,
  subCategoryHandler,
  useOnToolbar,
  dropDownTogglerHandler,
  showDropDown,
  subItems,
}) => {
  const [backgroundActive, setBackgroundActive] = useState(false);
  const [navItemBackground, setNavItemBackground] = useState(null);
  const [scrollY, setScrollY] = useState(0);

  useScrollPosition(
    ({ currPos }) => {
      setScrollY(currPos.y);
    },
    [showDropDown],
    false,
    false,
    0
  );

  //this is to avoid top scrolling when entering/leaving the dropdown
  useEffect(() => {
    document.body.style.position = "";
    document.body.style.top = "";
    window.scrollTo(scrollY, scrollY * -1);
    setNavItemBackground(false);
    if (showDropDown) {
      setTimeout(() => {
        setNavItemBackground(true);
      }, 500);
      document.body.style.position = "fixed";
      document.body.style.top = `${scrollY}px`;
    }
  }, [showDropDown]);

  const navItemMouseEnter = (id) => {
    setBackgroundActive(id);
  };

  const navItemMouseLeave = () => {
    setBackgroundActive(false);
  };

  const navItems = subItems.map(item => (
    <NavItem path={item.path} label={item.label} mouseEntered={() => navItemMouseEnter(item.id)} mouseLeft={navItemMouseLeft}/>
  ))

  return (
    <Wrapper>
      <List>
        {navItems}
      </List>
    </Wrapper>
  );
};
export default Dropdown;
