import React, { useEffect, useState } from "react";
import { useStore } from "react-redux";
import {
  Wrapper,
  Banner,
  BannerImage,
  Details,
  Title,
  Features,
  Feature,
  Panel,
  Add,
  Remove,
  Qty,
  CTA,
  Purchase,
} from "../../StyledComponents/productDetails";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusCircle, faMinusCircle } from "@fortawesome/free-solid-svg-icons";

const ProductDetails = ({ product }) => {
  const [qty, setQty] = useState(1);

  const qtyHandler = (mode) => {
    if (mode === "remove" && qty > 1) {
      setQty((qty) => qty - 1);
    }
    if (mode === "add") {
      setQty((qty) => qty + 1);
    }
  };
  return (
    <Wrapper>
      <Banner>
        <BannerImage src="/DetailsTest/test.jpg" alt="banner logo" />
      </Banner>
      <Details>
        <Title>Caf√© Black</Title>
        <Features>
          <Feature>Sobre</Feature>
          <Feature>Como usar</Feature>
          <Feature>Ingredientes</Feature>
          <Feature>Organic</Feature>
        </Features>
        <Panel>
          <Panel>
            <CTA>Compre</CTA>
            <Purchase>
              <Remove onClick={() => qtyHandler("remove")}>
                <FontAwesomeIcon icon={faMinusCircle} />
              </Remove>
              <Qty>{qty}</Qty>
              <Add onClick={() => qtyHandler("add")}>
                <FontAwesomeIcon icon={faPlusCircle} />
              </Add>
            </Purchase>
          </Panel>
        </Panel>
      </Details>
    </Wrapper>
  );
};

export default ProductDetails;

export async function getServerSideProps(context) {
  const product = context.query.product;
  const category = context.query.activeCategory;
  const id = context.query.id;
  const res = await fetch(
    `https://meli-99509.firebaseio.com/catalog/${product}/${category}/${id}.json`
  );
  const json = await res.json();

  return {
    props: {
      product: json,
    },
  };
}
