import styled, {css} from 'styled-components'
import { mainColors } from './Library/variables'

export const Wrapper = styled.section`
height: 100%;
width: 100%;
`

export const Banner = styled.div`
width: 100%;
height: 50vh;
`

export const BannerImage = styled.img`
object-fit: cover;
width: 100%;
height: 100%;
`

export const Details = styled.section`
padding-top: 2rem;
display: flex;
flex-direction: column;
align-items: center;
text-align: center;
min-height: 100vh;
`

export const Title = styled.h1`
font-size: 3rem;
margin: 1rem auto;
width: 80%;
z-index: 1;
text-align: center;

::after {

}
`

export const Features = styled.div`
display: grid;
grid-template-columns: repeat(auto-fit, minmax(5rem, 9rem));
grid-gap: .8rem;
width: 100%;
padding: .5rem;
justify-content: center;
align-content: center;
@media(min-width: 40rem) {
    grid-template-columns: repeat(auto-fit, 11rem);   
    grid-gap: 2rem;
    padding: 1.1rem;
}
`
const rotateFeat = keyframes`
to{}
`
export const Feature = styled.div`
cursor: pointer;
width: 100%;
height: 7rem;
background-color: ${mainColors.accentOrange};
border-radius: 25px;
color: white;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
transition: all 800ms ease-in-out;

:hover {
   animation: ${rotateFeat} 800ms ease-in forwards;  
   transform: rotateY(360deg);
}
`

export const Article = styled.article`

`
