import React, { Fragment } from "react";
// import Logo from "../../../UI/Logo/Logo";
// import Cart from "../../../UI/Cart/Cart";
// import Dropdown from "../Dropdown/Dropdown";
// import HamburgerButton from "../HamburgerButton/HamburgerButton";
import { useSelector, useDispatch } from "react-redux";
import {useShowNavigationItems} from '../../../store/actions/actionsIndex'
import {NavigationPanel, HBWr, HBInnerWr, HBSpan, Logo, LogoWr} from '../../../StyledComponents/toolbarItems'
import {Bee} from '../../../public/Icon/Icon'
import Link from 'next/link'

const HamburgerButton = ({active}) => {
  return (
       <HBInnerWr active={active}>
         <HBSpan />
         <HBSpan />
         <HBSpan />
       </HBInnerWr>
  )
}

const ProductLogo = () => (
  <Logo>
    <Link href="/" passHref>
    <Bee />
    </Link>
  </Logo>
)


const Toolbar = ({visible}) => {
 const onShowNavigationItems = useDispatch(useShowNavigationItems())
 const navItemsActive = useSelector(state => state.navigation.showNavItems);
 const navItems = useSelector(state => state.navigation.navItems)
  let toolbarIconHidden = {
    top: "-10vh",
  };

  let toolbarIconShow = {
    bottom: "0",
  };

  let topIconStyle = !visible ? { top: "-10vh"} : {bottom: 0};
  let bottomIconStyle = !visible ? {bottom: "-10vh"} : {bottom: "2rem"};

  const iconStyles = !visible ? toolbarIconHidden : toolbarIconShow;
  const navigationPanelClasses = ["NavigationPanel", navItemsActive ? "NavigationPanel__active" : null]
  const hamburgerButtonClasses= ["Hamburger__button__wrapper", navItemsActive ? "Hamburger__button__wrapper__active" : null]; //done
  const logoClasses = ["Logo__wrapper", navItemsActive ? "Logo__wrapper__active" : null];

  return (
    <Fragment>
      <NavigationPanel active={false}>
        <HamburgerButton active={false}/>
        <ProductLogo />
      </NavigationPanel>
      <HBWr active={false}>
        <HamburgerButton />
      </HBWr>
    </Fragment>
  );
};

export default Toolbar;


