import styled, { css } from "styled-components";

const navItemsFlexStyles = css`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const wrapperDims = css`
  width: 100vw;
  height: 100vh;
`;

export const Wrapper = styled.div`
  position: fixed;
  ${wrapperDims};
background: ${({withBackground}) => withBackground ? withBackground : rgba(86, 20, 238, 1)};
  ${navItemsFlexStyles};
`;

export const List = styled.ul`
  ${wrapperDims};
  margin: 0;
  padding: 0;
  display: flex;
  ${navItemsFlexStyles};
`;

export const MenuItem = styled.li`
  ${navItemsFlexStyles};
  margin: 1rem;
  font-weight: bold;
  font-size: 2.2rem;
  a {
    text-align: center;
    color: white;
    padding: 2rem;
  }
  &,
  a {
    width: 100vw;
  }
`;
