import React, { Fragment, useEffect, useState } from "react";
// import Dropdown from "../Dropdown/Dropdown";
import { useSelector, useDispatch } from "react-redux";
import { useShowNavigationItems } from "../../../store/actions/actionsIndex";
import {
  NavigationPanel,
  HBWr,
  HBInnerWr,
  HBSpan,
  Logo,
  LogoWr,
  Cart,
  CartCounter,
} from "../../../StyledComponents/toolbarItems";
import { Bee } from "../../../public/Icon/Icon";
import Link from "next/link";
import { faShoppingBag } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const HamburgerButton = ({ active }) => {
  return (
    <HBInnerWr active={active}>
      <HBSpan />
      <HBSpan />
      <HBSpan />
    </HBInnerWr>
  );
};

const ProductLogo = () => (
  <Logo>
    <Link href="/">
      <Bee />
    </Link>
  </Logo>
);

const ShoppingCart = ({ style }) => (
  <Cart style={style}>
    <FontAwesomeIcon icon={faShoppingBag} size="2x" />
    <CartCounter>
      <span>3</span>
    </CartCounter>
  </Cart>
);

const Toolbar = ({ visible }) => {
  const [globalWindow, setWindow] = useState(null);
  const onShowNavigationItems = useDispatch(useShowNavigationItems());
  const navItemsActive = useSelector((state) => state.navigation.showNavItems);
  const navItems = useSelector((state) => state.navigation.navItems);

  let toolbarIconHidden = {
    top: "-10vh",
  };

  let toolbarIconShow = {
    bottom: "0",
  };

  useEffect(() => {
    setWindow(window);
  }, []);

  let topIconStyle = !visible ? { top: "-10vh" } : { bottom: 0 };
  let bottomIconStyle = !visible ? { bottom: "-10vh" } : { bottom: "2rem" };
  const iconStyles = !visible ? toolbarIconHidden : toolbarIconShow;

  return (
    <Fragment>
      <NavigationPanel active={true} style={iconStyles}>
        <ProductLogo />
        <HamburgerButton active={true} />
      </NavigationPanel>
      <HBWr active={true}>
        <HamburgerButton />
      </HBWr>
      <LogoWr active={true} style={iconStyles}>
        <ProductLogo />
      </LogoWr>
      <ShoppingCart
        style={globalWindow < 640 ? bottomIconStyle : topIconStyle}
      />
    </Fragment>
  );
};

export default Toolbar;
