import styled, { css } from "styled-components";
import { mainColors } from "./Library/variables";

const colorPattern = (pattern) => {
  const footer = css`
    color: black;
    :hover {
      color: ${mainColors.accentBackground};
    }
  `;
  const menu = css`
    color: white;
    :hover {
      color: ${mainColors.accentOrange};
    }
  `;
  return pattern === 'footer' ? footer : pattern === 'menu' ? menu : alert('check color pattern ')
};

export const SocialMediaTitle = styled.h4`
  grid-area: social-title;
  margin: 1rem;
`;

export const SocialMediaWrapper = styled.ul`
  padding-left: 0;
  grid-area: social;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

export const LinkWrapper = styled.li`
  display: inline-block;
  margin: 0 1rem;
`;

export const Link = styled.a`
  display: inline-block;
  text-decoration: none;
  color: black;
  :hover {
    color: ${mainColors.accentBackground};
    transform: scale(1.2);
  }
`;
