import React, { useState } from "react";
import Toolbar from "../Navigation/Toolbar/Toolbar";
import Footer from "../../Components/Footer/Footer";
import { connect } from "react-redux";
import { useScrollPosition } from "../../Hooks/useScroll/useScrollPosition";

const Layout = (props) => {
  const [visible, setVisible] = useState(true);

  useScrollPosition(
    ({ prevPos, currPos }) => {
      const isShow = currPos.y > prevPos.y;
      if (isShow !== visible) setVisible(isShow);
    },
    [ visible],
    false,
    false,
    150
  );

  return (
    <div>
      <Toolbar visible={visible}  />
      <main>{props.children}</main>
      <Footer />
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    drawerActive: state.navigationReducer.showSideDrawer,
    hamButtonTouched: state.navigationReducer.hamButtonClicked,
  };
};

export default connect(mapStateToProps)(Layout);
