import styled, { css } from "styled-components";

const navItemsFlexStyles = css`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

export const Wrapper = styled.div`
  position: fixed;
  width: 100vw;
  height: 100vh;
  background-color: rgba(86, 20, 238, 1);
  ${navItemsFlexStyles};
`;

export const List = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  ${navItemsFlexStyles};
`;

export const MenuItem = styled.li`
${navItemsFlexStyles}
margin: 2rem;
color: white;
font-weight: bold;
`;
