import React, { useEffect } from "react";
import { useStore } from "react-redux";
import { useRouter } from "next/router";
import {
  Wrapper,
  Banner,
  BannerImage,
  Details,
  Title,
} from "../../StyledComponents/productDetails";

const ProductDetails = ({product}) => {
  console.log("initial props", product);

return <div>{product}</div>
};

export default ProductDetails;

ProductDetails.getInitialProps =  async (context) => {
  const product = context.query.product;
  const category = context.query.activeCategory;
  const id = context.query.id;
  const res = await fetch(`https://meli-99509.firebaseio.com/catalog/${product}/${category}/${id}.json`)
  const json = await res.json()

  return {
    product: json
  };
};
