import styled from 'styled-components'

export const Layout = styled.div`
  min-height: 100%;
  display: grid;
  grid: {
    template-rows: 0rem auto 11rem; //changed from 3rem to 0
    template-areas: "header" "main" "footer";
  }

  & footer {
    grid-area: footer;
  }


main {
  grid-area: main;
  height: 100%;
}

@media (min-width: 40rem) {
  .Layout {
    grid-template-rows: 0rem auto 11rem; //from 4rem to 0
  }
}
`
