import { setState } from "../../Utilities/utilities";
import * as actions from "../actions/navigationActions";
import Home from '../../Content/NavigationItems/Home/IMG_0522_Original.jpg'
import Login from '../../ ../../Content/NavigationItems/Login/IMG_20140422_120241629_HDR_Original.jpg'
import About from '../../Content/NavigationItems/About/IMG_20140419_124250_Original.jpg'
import Faq from '../../Content/NavigationItems/Faq/IMG_20140226_141855262_HDR_Original.jpg'

const initialState = {
  showNavItems: false,
  navItems: [
    {
      id: "Home",
      label: "INÍCIO",
      path: "/",
      background: Home
     },
     {
      id: "login",
      label: "LOGIN",
      path: "/Login",
      background: Login
     },
     {
      id: "about",
      label: "SOBRE NÓS",
      path: "/sobre-nos",
      background: About
     },
     {
      id: "faq",
      label: "FAQ",
      path: "/faq",
      background: Faq
     }
  ]
};

const toggleSideDrawer = (state, action) => {
  return setState(state, {
    showNavItems: !state.showNavItems,
  });
};

export const navigationReducer = (state = initialState, action) => {
  switch (action.type) {
    case actions.SHOW_NAVIGATION_ITEMS:
      return toggleSideDrawer(state, action);
    default:
      return state;
  }
};
