import React from "react";
import Link from "next/link";
import styled, { css, ThemeProvider } from "styled-components";
import NavLink from "../Components/NavLink/NavLink";
import {
  MyLink,
  MyButton,
  CoolTitle,
  Section,
  DangerButton,
} from "../Components/StyledComponents/styledComponents";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4rem;
`;

const Input = styled.input.attrs({
  type: "number",
  size: 2,
  placeholder: "Your number",
})`
  color: red;
  text-decoration: none;
  :disabled {
    border: red 20px solid;
  }
  ${({ foo, borderWidth }) =>
    foo &&
    css`
      color: purple;
      padding: 0.3rem;
      outline: none;
      border-width: ${borderWidth};
      ::-webkit-inner-spin-button,
      ::-webkit-outer-spin-button {
        -webkit-appearance: none;
      }
      ::placeholder {
        color: purple;
      }
      &:focus {
        border-color: red;
        padding: 0.3rem;
      }
    `}
`;
const Label = styled.label`
padding: 1rem;
`;

const Div = styled.div.withConfig({
  shouldForwardProp: (prop, defaultValidatorFn) =>
    !["foo"].includes(prop) && defaultValidatorFn(prop),
})`
  margin: 1rem;
  padding: 1rem;
  ${Label}:hover {
      background-color: red;
  }

  ${({ foo, theme }) => `
 background-color: ${foo};
 border: 1px solid ${theme.color};
 color: ${theme.font}
 `}
`;

const FormInput = styled.input.attrs({
  type: "text",
})`
  border-color: blue;
  color: red;
  background-color: ${(props) => props.theme.backgroundColor};
  ${({ $size }) => `
  height: ${$size}rem;
  width: ${$size}rem;;
  `};
`;

const theme = {
  color: "red",
  font: "green",
};

const invertedTheme = ({ color, font }) => ({
  color: font,
  font: color,
});

const App = () => {
  return (
    <Wrapper>
      <ThemeProvider theme={theme}>
        <Div foo="yellow">Styled Div</Div>
        <ThemeProvider theme={invertedTheme}>
          <Div>Inverted div</Div>
        </ThemeProvider>
      </ThemeProvider>
      <Input foo borderWidth="3px" />
      <Link href={{ pathname: "/about", query: { name: "test" } }}>
        <a>About us</a>
      </Link>
      <FormInput as="div" $size="4">
        aaaa
      </FormInput>
      <Label as="label">Label</Label>
    </Wrapper>
  );
};

export default App;
