import React, { useState } from "react";
import Toolbar from "../Navigation/Toolbar/Toolbar";
import Footer from "./Footer";
import { useScrollPosition } from "../../Hooks/useScrollPosition";
import {Wrapper} from '../../StyledComponents/layoutItems'

const Layout = (props) => {
  const [visible, setVisible] = useState(true);
  const [currentPositionY, setCurrentPositionY] = useState(null)

  useScrollPosition(
    ({ prevPos, currPos }) => {
      setCurrentPositionY(currPos.y)
      const isShow = currPos.y > prevPos.y;
      if (isShow !== visible) setVisible(isShow);
    },
    [visible],
    false,
    false,
    150
  );

  return (
    <Wrapper>
      <Toolbar visible={visible}  currPos={curr}/>
      <main>{props.children}</main>
      <Footer />
    </Wrapper>
  );
};

export default Layout;
