import styled, {css} from 'styled-components';
import {toolBarMixin} from './Library/mixins';
import {mainColors} from './Library/variables'


export const NavigationPanel = styled.div`
 display: none;

 @media(min-width: 40rem) {
     ${toolBarMixin(1, 1)};
      width: 9rem;
      height: 4rem;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: #fdf3e6;
      border-radius: 35px;
      z-index: 35;
      transition: all 500ms ease-in;

      &:hover {
        background-color: purple;
      }
 }
`

export const HamburgerButtonWrapper = styled.div`
 ${toolBarMixin(null, 1)};
  width: 3.2rem;
  height: 3.2rem;
  border-radius: 50%;
  background-color: #fdf3e6;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 35;
  ${({active}) => active && css`
  background-color: #f29b38
  `}
`
