import styled, {css} from 'styled-components'
import { mainColors } from './Library/variables'

export const Wrapper = styled.section`
height: 100%;
width: 100%;
`

export const Banner = styled.div`
width: 100%;
height: 50vh;
`

export const BannerImage = styled.img`
object-fit: cover;
width: 100%;
height: 100%;
`

export const Details = styled.section`
padding-top: 2rem;
display: flex;
flex-direction: column;
align-content: center;
text-align: center;
min-height: 100vh;
`

export const Title = styled.h1`
font-size: 4rem;
margin: 0;
width: 80%;
height: 4rem;
z-index: 1;

::after {

}
`

export const TitleWrapper = styled.div`
margin: 1rem auto;
height: 10rem;
width: 80vw;
background: rgba(242, 155, 56, .5);
border-radius: 10px;
position: relative;
box-shadow: 6px 6px 2px yellow;
display: flex;
flex-direction
`

