import {css} from 'styled-components';
import {zIndex} from './variables'

export const toolBarMixin = (top = null, left = null, right = null, bottom = null) => {
   return css`
    position: fixed;
    top: ${top}rem;
    left: ${left}rem;
    right: ${right}rem;
    bottom: ${bottom}rem;
    z-index: ${zIndex.toolbar};
    transition: all 400ms ease-in;
   `
  }

  export const hamburgerSpanMixin = (nthChildVal) => {
    switch(nthChildVal) {
        case 1: 
        return css`
      transform-origin: left;
      transform: rotate(45deg) translate(5%, -150%);
    case 2:
      transform: translateX(-100%);
      opacity: 0;

      case: 3

    span:nth-child(3) {
      transform-origin: left;
      transform: rotate(-45deg) translate(5%, 150%);
    }
        `
    }
  }
