import styled, { css } from "styled-components";

export const MyLink = styled.a`
  color: red;

  ${(props) =>
    props.primary &&
    css`
       {
        color: green;
    `}
`;

export const MyButton = styled.button`
  background-color: blue;
  color: white;
  font-weight: strong;

  ${(props) =>
    props.success &&
    css`
      color: green;
      background-color: yellow;
    `}
`;

export default {
  MyLink,
  MyButton,
};
