import React, { useState } from "react";
import {
  Wrapper,
  Banner,
  BannerImage,
  Details,
  Title,
  Features,
  Feature,
  Panel,
  Add,
  Remove,
  Qty,
  CTA,
  Purchase,
} from "../../StyledComponents/productDetails";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusCircle, faMinusCircle } from "@fortawesome/free-solid-svg-icons";
import { Backdrop } from "../../StyledComponents/main";
import FeaturesDB from "../../DB/catalog.json";
import styled, {css} from "styled-components";

const flexMixin = css`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Column = styled.div`
  display: flex;
  flex-direction: column;
`;

const Cel = styled.div`
  height: 4rem;
  width: 6rem;
  border: 1px solid black;
  ${flexMixin};
`;

const Span = styled.span`
  padding: 2rem;
  font-weight: bold;
`;

const CellSpan = styled.input`
width: 100%;


`

const ColTitle = styled.div`
  width: 6rem;
  height: 2rem;
  text-align: center;
  border: 1px solid black;
  ${flexMixin};
`;

const ProductDetails = ({ product, category, id }) => {
  const [qty, setQty] = useState(1);
  const [activeSpec, setActiveSpec] = useState(0);
  const [toggledSpec, setToggledSpec] = useState(false);

  const ProdDetails = FeaturesDB.details[product][category][id];

  const qtyHandler = (mode) => {
    if (mode === "remove" && qty > 1) {
      setQty((qty) => qty - 1);
    }
    if (mode === "add") {
      setQty((qty) => qty + 1);
    }
  };

  const featureHandler = (index) => {
    index !== activeSpec && setToggledSpec(true);
    index === activeSpec && setToggledSpec((state) => !state);
    setActiveSpec(index);
  };

  const info = [
    { column: "A", rows: ["a1", "a2"] },
    { column: "B", rows: ["b1", "b2"] },
  ];

  const table = info.map((col) => (
    <Column key={col.column}>
      <ColTitle>
        <Span>{col.column}</Span>
      </ColTitle>
      {col.rows.map((row, index) => (
        <Cel key={index}>
          <CellSpan placeholder="digite alguma merda aqui"/>
        </Cel>
      ))}
    </Column>
  ));

  const feats = ["Sobre", "Como usar", "Ingredientes", "Reviews"];
  const features = feats.map((feature, i) => (
    <Feature
      index={i}
      active={i === activeSpec && toggledSpec}
      inactive={i != activeSpec && toggledSpec}
      onClick={() => featureHandler(i)}
      key={i}
    >
      <span>{feature}</span>
    </Feature>
  ));

  return (
    <Wrapper>
      <div
        style={{
          display: "flex",
          width: "100vw",
          height: "100vh",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        {table}
      </div>
      {/* <Banner>
        <BannerImage src="/DetailsTest/test.jpg" alt="banner logo" />
        <Title>Café Black</Title>
        <Backdrop style={{ zIndex: 1 }} forElement={true} />
      </Banner>
      <Details>
        <Features index={activeSpec} activeFeature={toggledSpec}>{features}</Features>
        <Panel>
          <CTA>Compre</CTA>
          <Purchase>
            <Remove onClick={() => qtyHandler("remove")}>
              <FontAwesomeIcon icon={faMinusCircle} />
            </Remove>
            <Qty>{qty}</Qty>
            <Add onClick={() => qtyHandler("add")}>
              <FontAwesomeIcon icon={faPlusCircle} />
            </Add>
          </Purchase>
        </Panel>
      </Details> */}
    </Wrapper>
  );
};

export default ProductDetails;

export async function getServerSideProps(context) {
  const product = context.query.product;
  const category = context.query.activeCategory;
  const id = context.query.id;
  // const res = await fetch(
  //   `https://meli-99509.firebaseio.com/catalog/${product}/${category}/${id}.json`
  // );
  // const json = await res.json();

  return {
    props: {
      product,
      category,
      id,
    },
  };
}
