import styled, { css, keyframes } from "styled-components";
import { mainColors } from "./Library/variables";

export const Wrapper = styled.section`
  height: 100%;
  width: 100%;
`;

export const Banner = styled.div`
  width: 100%;
  height: 40vh;
`;

export const BannerImage = styled.img`
  object-fit: cover;
  width: 100%;
  height: 100%;
`;

export const Details = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  min-height: 100vh;
`;

export const Title = styled.h1`
  font-size: 3rem;
  margin: 1rem auto;
  width: 80%;
  z-index: 1;
  text-align: center;
`;

export const Features = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(5rem, 9rem));
  grid-gap: 0.8rem;
  width: 100%;
  padding: 0.5rem;
  justify-content: center;
  align-content: center;
  @media (min-width: 40rem) {
    grid-template-columns: repeat(auto-fit, 11rem);
    grid-gap: 2rem;
    padding: 1.1rem;
  }
`;
const rotateFeat = keyframes`
to{transform: rotateY(360deg)}
`;
export const Feature = styled.div`
  cursor: pointer;
  width: 100%;
  height: 7rem;
  background-color: #f7c68e;
  border-radius: 25px;
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  transition: all 400ms ease-in-out;
  font-weight: bold;
  font-size: 1.3rem;

  :hover {
    animation: ${rotateFeat} 800ms ease-in forwards;
  }
`;
export const Panel = styled.div`
  display: flex;
  background-color: ${mainColors.accentBackground};
  border-radius: 25px;
  overflow: hidden;
  align-items: center;
  justify-content: center;
  width: 80%;
  color: white;
  max-width: 30rem;
  padding: 0.2rem;
`;

const ctaAnimation = keyframes`
to {width: 50%}
`;
export const CTA = styled.button`
  width: 100%;
  height: 4rem;
  background-color: ${mainColors.accentBackground};
  align-content: center;
  font-size: 2rem;
  color: white;
  cursor: pointer;
  position: relative;
  :hover {
    ::after {
      width: 0%;
      animation: ${ctaAnimation} 400ms forwards ease-in;
      position: absolute;
      content: "";
      height: 0.5rem;
      background-color: white;
      bottom: 0%;
      left: 50%;
      transform: translateX(-50%);
      border-radius: 10px;
    }
  }
`;

export const Purchase = styled.div`
  display: flex;
`;

export const Add = styled.button`
  font-size: 2rem;
  color: white;
  cursor: pointer;
`;

export const Remove = styled.button`
font-size: 2rem;
  color: white;
  cursor: pointer;`;
export const Qty = styled.span`
  grid-area: Qty;
  align-self: center;
  font-size: 2rem;
`;
export const Article = styled.article``;
