import React, { Fragment, useState, useEffect } from "react";
import { useScrollPosition } from "../../Hooks/useScrollPosition";
import { Transition } from "react-transition-group";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";
import Link from "next/link";
import {
  Wrapper,
  List,
  MenuItem,
  Background,
} from "../../StyledComponents/dropdownItems";

const NavItem = ({ path, label, mouseEntered, mouseLeft, key }) => {
  return (
    <MenuItem key={key} onMouseLeave={mouseLeft} onMouseEnter={mouseEntered}>
      <Link href={path}>
        <a>{label}</a>
      </Link>
    </MenuItem>
  );
};

const Dropdown = ({
  dropDownTitle,
  subCategoryHandler,
  useOnToolbar,
  dropDownTogglerHandler,
  showDropDown,
  subItems,
}) => {
  const [backgroundImage, setBackgroundImage] = useState(false);
  const [scrollY, setScrollY] = useState(0);

  useScrollPosition(
    ({ currPos }) => {
      setScrollY(currPos.y);
    },
    [showDropDown],
    false,
    false,
    0
  );
  //this is to avoid top scrolling when entering/leaving the dropdown
  useEffect(() => {
    document.body.style.position = "";
    document.body.style.top = "";
    window.scrollTo(scrollY, scrollY * -1);
    if (showDropDown) {
      document.body.style.position = "fixed";
      document.body.style.top = `${scrollY}px`;
    }
  }, [showDropDown]);

  const navItemMouseEnter = (path) => {
    setBackgroundImage(path);
  };

  const navItemMouseLeave = () => {
    setBackgroundImage(false);
  };

  const navItems = subItems.map((item) => (
    <>
      <NavItem
        keys={item.id}
        path={item.path}
        label={item.label}
        mouseEntered={() => navItemMouseEnter(item.background)}
        mouseLeft={navItemMouseLeave}
      />
      <Background
        key={item.id}
        withBackground={backgroundImage === item.background}
        backgroundImage={item.background}
      />
    </>
  ));

  return (
    <Wrapper withBackground={backgroundImage}>
      <List>{navItems}</List>
    </Wrapper>
  );
};
export default Dropdown;
